hslToRgb=function(r,a,t){var o=Math.min(r,359)/60,h=(1-Math.abs(2*t-1))*a,n=h*(1-Math.abs(o%2-1)),f=t-.5*h,u=f,M=f,b=f;return o<1?(u+=h,M=+n,b+=0):o<2?(u+=n,M+=h,b+=0):o<3?(u+=0,M+=h,b+=n):o<4?(u+=0,M+=n,b+=h):o<5?(u+=n,M+=0,b+=h):o<6?(u+=h,M+=0,b+=n):(u=0,M=0,b=0),"rgb("+Math.floor(255*u)+", "+Math.floor(255*M)+", "+Math.floor(255*b)+")"},createSpectrum=function(r){for(var a=[],t=270/r,o=1;o<=r;o++){var h=hslToRgb(o*t,.5,.5);a.push(h)}return a};